import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
class app extends Component{
  constructor(props){
    super(props);
    this.state={
      users:[]
  }
}

API_URL="https://localhost:44309/";

componentDidMount(){
  this.refreshUsers();
}
async refreshUsers(){
  fetch(this.API_URL+"Job/Portal/api/users").then(response=>response.json())
  .then(data=>{
    this.setState({users:data});
  })
}
render() {
  const{users}=this.state;
  return (
    <div className="App">
     <h2>Hello</h2>

     {users.map(user=>
      <p>
        <b>* user.description</b>
      </p>
      )}
    </div>
  );
}
}
export default app;
